import styled from "styled-components";

export const CardHomeStyle = styled.div`
    width: 80%;
    margin:  0 auto;
    background-color: white;
    box-shadow: 0 .3rem .5rem rgba(1,1,1,0.15);
    margin-bottom: 1rem ;
    animation: showin 1s ease-in .5s;
    @media screen  and (min-width:40rem) {
        width: 18%;

    }
    @keyframes showin {
        0% {
            opacity: 0;
        }
        100% {
            opacity: 100%;
        }
    }
`
