import styled from "styled-components";

export const NavStyle = styled.nav`
    width: 100%;
    padding: 2rem 2rem;
    box-shadow:.4rem 0 1rem rgba(1,1,1,.15);
    font-size: 1.2rem;
`

export const ContainerStyle = styled.div`
    max-width: 90rem;
    display: flex;
    margin: 0 auto;
    justify-content: space-between;

`
