import styled from "styled-components";

const StyledButton = styled.button`
    border:none;
    background-color:white;
    box-shadow:0 .2rem 1rem rgba(1,1,1,.15);
    padding: 1rem 2rem;
    font-size: 1rem;
    &:hover {
        transform: scale(.98);
    }
`
export default StyledButton;
