import React ,{useEffect,useState} from "react";
import {useParams,useNavigate} from 'react-router-dom';
import {ContainerStyle } from "../styled/ContainerStyle";
import RecipeCard from "./RecipeCard";
const Meal:React.FC = () => {
    const [recipe, setRecipe] = useState(null)
    const id = useParams()
    useEffect(()=>{
        fetch('www.themealdb.com/api/json/v1/1/lookup.php?i='+id)
        .then(response =>response.json())
        .then(data =>setRecipe(data))


      },[id])
    return(
        <ContainerStyle>
                <RecipeCard key={recipe.idMeal} strMealThumb={recipe.strMealThumb} idMeal={recipe.idMeal} strMeal={recipe.strMeal}/>
        </ContainerStyle>

    )
}
export default Meal;
