import styled from "styled-components";

export const StyledButton = styled.button`
    border:none;
    background-color:#F8A607;
    color: white;
    box-shadow:0 .2rem 1rem rgba(1,1,1,.15);
    padding: 1rem 2rem;
    font-size: 1rem;
    &:hover {
        transform: scale(.88);
    }
`
