import React ,{useEffect} from "react";
import {ContainerStyle } from "../styled/ContainerStyle";
import axios from 'axios';
import { useDispatch,useSelector } from "react-redux";
import {setRecipesAction} from "../redux/actions/setRecipesAction"
import RecipeCard from "./RecipeCard";
import './list.css'
const List:React.FC = () => {
    const state:any = useSelector<any>(state => state.recipes)
    const dispatch = useDispatch()
    console.log(state);
    const fetchData =async () => {
        const data = await axios.get('https://www.themealdb.com/api/json/v1/1/search.php?f=a').catch(err =>console.log(err))
        console.log(data);

    }
    useEffect(()=>{
    fetch("https://www.themealdb.com/api/json/v1/1/search.php?f=a")
    .then(response => response.json())
    .then(result => dispatch(setRecipesAction(result.meals)))
    .catch(err => {
      console.error(err);
    });
  },[])
type RECIPE = {
    idMeal:number,
    strCategory:string,
    strMeal:string,
    strMealThumb:string
}
    return(
        <ContainerStyle>
            <h1>List</h1>
            {state.map((recipe : RECIPE)=>{
                return <RecipeCard idMeal={recipe.idMeal} strMeal={recipe.strMeal}/>
            })}
        </ContainerStyle>

    )
}
export default List;
