import {useState,useEffect} from 'react';
import Globelstyles from './styled/Globelstyles';
import Button from './components/Button'
import Meal from './components/Meal';
function App(){
  const [meals, setMeals] = useState<any[]>([])
  useEffect(()=>{
    fetch("https://www.themealdb.com/api/json/v1/1/search.php?f=a")
    .then(response => response.json())
    .then(result => setMeals(result.meals))
    .catch(err => {
      console.error(err);
    });
  },[])
  console.log(meals);

  return (
    <div>
      <Globelstyles />
      <Button />
      {meals && meals.map(meal =>{
        return <Meal key={meal.idMeal} idMeal={meal.idMeal} strCategory={meal.strCategory} strMealThumb={meal.strMealThumb} strMeal={meal.strMeal}/>

      })}
    </div>
  );
}

export default App;
