import React ,{useEffect,useState} from "react";
import {useParams,useNavigate} from 'react-router-dom';
import {ContainerStyle } from "../styled/ContainerStyle";

const Meal:React.FC = () => {
    const [recipe, setRecipe] = useState<any>(null)
    const {id} = useParams()
    const url =" https://www.themealdb.com/api/json/v1/1/lookup.php?i=" + id
    console.log(url);

    useEffect(()=>{
        fetch('https://www.themealdb.com/api/json/v1/1/lookup.php?i='+id)
        .then(response =>response.json())
        .then(data =>{
            console.log(data.meals[0]);

            setRecipe(data.meals[0])

        })
        console.log(recipe);

      },[id])


    return(
        <div className="content">

        <ContainerStyle>
            {recipe &&  <RecipeCard key={recipe.idMeal} strMealThumb={recipe.strMealThumb} idMeal={recipe.idMeal} strMeal={recipe.strMeal}/>}

           {recipe && 
           <div className="side">
               <img src={recipe.strMealThumb} alt={recipe.strMeal} srcset="" />
           </div>
           }
        </ContainerStyle>
        </div>
    )
}
export default Meal;
